#ifndef BANDIT_VERSION_H
#define BANDIT_VERSION_H

#define BANDIT_MAJOR 3
#define BANDIT_MINOR 0
#define BANDIT_PATCH 0

#define BANDIT_TOSTRING(x) #x
#define BANDIT_MACROTOSTRING(x) BANDIT_TOSTRING(x)
#define BANDIT_VERSION \
  BANDIT_MACROTOSTRING(BANDIT_MAJOR) "." \
  BANDIT_MACROTOSTRING(BANDIT_MINOR) "." \
  BANDIT_MACROTOSTRING(BANDIT_PATCH) "-snapshot"

#endif
